import{_ as s,c as n,o as a,d as e}from"./app.aa7b13f3.js";const F=JSON.parse('{"title":"UI Manager","description":"","frontmatter":{},"headers":[{"level":2,"title":"Requiring","slug":"requiring","link":"#requiring","children":[]},{"level":2,"title":"Using","slug":"using","link":"#using","children":[]},{"level":2,"title":"Elements","slug":"elements","link":"#elements","children":[{"level":3,"title":"Action Elements","slug":"action-elements","link":"#action-elements","children":[]},{"level":3,"title":"Game Elements","slug":"game-elements","link":"#game-elements","children":[]}]}],"relativePath":"hammerstone/ui-manager.md","lastUpdated":1663807037000}'),l={name:"hammerstone/ui-manager.md"},p=e(`<h1 id="ui-manager" tabindex="-1">UI Manager <a class="header-anchor" href="#ui-manager" aria-hidden="true">#</a></h1><p>UI in Sapiens is fairly complex. The UI manager doesn&#39;t help you build UIs, it just helps you manage their state. For example:</p><ul><li>Giving you mouse control when the UI is active</li><li>Allowing you to press ESC to close the current UI (coming soon)</li><li>Preventing OTHER ui from showing on top of your currently active UI (coming soon)</li></ul><h2 id="requiring" tabindex="-1">Requiring <a class="header-anchor" href="#requiring" aria-hidden="true">#</a></h2><div class="language-lua"><button class="copy"></button><span class="lang">lua</span><pre><code><span class="line"><span style="color:#89DDFF;">local</span><span style="color:#A6ACCD;"> uiManager </span><span style="color:#89DDFF;">=</span><span style="color:#A6ACCD;"> </span><span style="color:#82AAFF;">mjrequire</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">hammerstone/ui/uiManager</span><span style="color:#89DDFF;">&quot;</span></span>
<span class="line"></span></code></pre></div><h2 id="using" tabindex="-1">Using <a class="header-anchor" href="#using" aria-hidden="true">#</a></h2><p>The uiManager relies on the concept of &#39;elements&#39;. A view is a place where UI can be put. For example, a <code>GameElement</code> covers (almost) the whole screen and captures the mouse.</p><h2 id="elements" tabindex="-1">Elements <a class="header-anchor" href="#elements" aria-hidden="true">#</a></h2><h3 id="action-elements" tabindex="-1">Action Elements <a class="header-anchor" href="#action-elements" aria-hidden="true">#</a></h3><p>Action Elements are rendered beside the radial menu that appears when selecting an object in the world. To register an Action Element, call <code>registerActionElement</code> on the <code>uiManager</code> like this:</p><div class="language-lua"><button class="copy"></button><span class="lang">lua</span><pre><code><span class="line"><span style="color:#89DDFF;">local</span><span style="color:#A6ACCD;"> exampleActionElement </span><span style="color:#89DDFF;">=</span><span style="color:#A6ACCD;"> </span><span style="color:#82AAFF;">mjrequire</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">exampleMod/exampleActionElement</span><span style="color:#89DDFF;">&quot;</span></span>
<span class="line"><span style="color:#FFCB6B;">uiManager</span><span style="color:#A6ACCD;">:</span><span style="color:#82AAFF;">registerActionElement</span><span style="color:#A6ACCD;">(exampleActionElement);</span></span>
<span class="line"></span></code></pre></div><p>The Action Element module should look something like this:</p><div class="language-lua"><button class="copy"></button><span class="lang">lua</span><pre><code><span class="line"><span style="color:#676E95;">-- Module setup</span></span>
<span class="line"><span style="color:#89DDFF;">local</span><span style="color:#A6ACCD;"> exampleActionElement </span><span style="color:#89DDFF;">=</span><span style="color:#A6ACCD;"> {</span></span>
<span class="line"><span style="color:#89DDFF;">	</span><span style="color:#676E95;">-- Required by the UI Manager</span></span>
<span class="line"><span style="color:#A6ACCD;">	view </span><span style="color:#89DDFF;">=</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">nil</span><span style="color:#A6ACCD;">,</span></span>
<span class="line"></span>
<span class="line"><span style="color:#89DDFF;">	</span><span style="color:#676E95;">--  Required by the UI Manager</span></span>
<span class="line"><span style="color:#A6ACCD;">	name </span><span style="color:#89DDFF;">=</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">Example Action Element</span><span style="color:#89DDFF;">&quot;</span></span>
<span class="line"><span style="color:#A6ACCD;">}</span></span>
<span class="line"></span>
<span class="line"><span style="color:#676E95;">-- Requires</span></span>
<span class="line"><span style="color:#676E95;">-- Add requires here</span></span>
<span class="line"></span>
<span class="line"><span style="color:#676E95;">-- This function is called automatically from the UI manager</span></span>
<span class="line"><span style="color:#89DDFF;">function</span><span style="color:#A6ACCD;"> </span><span style="color:#FFCB6B;">exampleActionElement</span><span style="color:#A6ACCD;">:</span><span style="color:#82AAFF;">initActionElement</span><span style="color:#89DDFF;">(</span><span style="color:#A6ACCD;">viewContainer</span><span style="color:#89DDFF;">,</span><span style="color:#A6ACCD;"> </span><span style="color:#A6ACCD;">gameUI</span><span style="color:#89DDFF;">,</span><span style="color:#A6ACCD;"> </span><span style="color:#A6ACCD;">hubUI</span><span style="color:#89DDFF;">,</span><span style="color:#A6ACCD;"> </span><span style="color:#A6ACCD;">world</span><span style="color:#89DDFF;">)</span></span>
<span class="line"><span style="color:#89DDFF;">	</span><span style="color:#676E95;">-- Create a parent container</span></span>
<span class="line"><span style="color:#A6ACCD;">	self.</span><span style="color:#FFCB6B;">view</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">=</span><span style="color:#A6ACCD;"> View.</span><span style="color:#82AAFF;">new</span><span style="color:#A6ACCD;">(viewContainer)</span></span>
<span class="line"></span>
<span class="line"><span style="color:#89DDFF;">    </span><span style="color:#676E95;">-- Add any button/other UI components here</span></span>
<span class="line"><span style="color:#89DDFF;">end</span></span>
<span class="line"></span>
<span class="line"><span style="color:#676E95;">-- Module return</span></span>
<span class="line"><span style="color:#89DDFF;">return</span><span style="color:#A6ACCD;"> exampleActionElement</span></span>
<span class="line"></span></code></pre></div><h3 id="game-elements" tabindex="-1">Game Elements <a class="header-anchor" href="#game-elements" aria-hidden="true">#</a></h3><p>Game Elements are shown nearly fullscreen to the user. To register a Game Elemenet, call <code>registerGameElement</code> on the <code>uiManager</code> like this:</p><div class="language-lua"><button class="copy"></button><span class="lang">lua</span><pre><code><span class="line"><span style="color:#89DDFF;">local</span><span style="color:#A6ACCD;"> exampleGameElement </span><span style="color:#89DDFF;">=</span><span style="color:#A6ACCD;"> </span><span style="color:#82AAFF;">mjrequire</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">exampleMod/exampleGameElement</span><span style="color:#89DDFF;">&quot;</span></span>
<span class="line"><span style="color:#FFCB6B;">uiManager</span><span style="color:#A6ACCD;">:</span><span style="color:#82AAFF;">registerActionElement</span><span style="color:#A6ACCD;">(exampleGameElement);</span></span>
<span class="line"></span></code></pre></div><p>The Game Element module should look something like this:</p><div class="language-lua"><button class="copy"></button><span class="lang">lua</span><pre><code><span class="line"><span style="color:#89DDFF;">local</span><span style="color:#A6ACCD;"> exampleActionElement </span><span style="color:#89DDFF;">=</span><span style="color:#A6ACCD;"> {</span></span>
<span class="line"><span style="color:#A6ACCD;">    gameUI </span><span style="color:#89DDFF;">=</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">nil</span><span style="color:#A6ACCD;">,</span></span>
<span class="line"><span style="color:#A6ACCD;">	name </span><span style="color:#89DDFF;">=</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">exampleActionElement</span><span style="color:#89DDFF;">&quot;</span><span style="color:#A6ACCD;">,</span></span>
<span class="line"><span style="color:#A6ACCD;">	view </span><span style="color:#89DDFF;">=</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">nil</span><span style="color:#A6ACCD;">,</span></span>
<span class="line"><span style="color:#A6ACCD;">}</span></span>
<span class="line"></span>
<span class="line"><span style="color:#676E95;">-- Requires</span></span>
<span class="line"><span style="color:#89DDFF;">local</span><span style="color:#A6ACCD;"> mjm </span><span style="color:#89DDFF;">=</span><span style="color:#A6ACCD;"> </span><span style="color:#82AAFF;">mjrequire</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">common/mjm</span><span style="color:#89DDFF;">&quot;</span></span>
<span class="line"><span style="color:#89DDFF;">local</span><span style="color:#A6ACCD;"> vec3 </span><span style="color:#89DDFF;">=</span><span style="color:#A6ACCD;"> mjm.</span><span style="color:#FFCB6B;">vec3</span></span>
<span class="line"><span style="color:#89DDFF;">local</span><span style="color:#A6ACCD;"> vec2 </span><span style="color:#89DDFF;">=</span><span style="color:#A6ACCD;"> mjm.</span><span style="color:#FFCB6B;">vec2</span></span>
<span class="line"><span style="color:#676E95;">-- Add more requires here</span></span>
<span class="line"></span>
<span class="line"><span style="color:#676E95;">-- Local state</span></span>
<span class="line"><span style="color:#89DDFF;">local</span><span style="color:#A6ACCD;"> backgroundWidth </span><span style="color:#89DDFF;">=</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">1140</span></span>
<span class="line"><span style="color:#89DDFF;">local</span><span style="color:#A6ACCD;"> backgroundHeight </span><span style="color:#89DDFF;">=</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">640</span></span>
<span class="line"><span style="color:#89DDFF;">local</span><span style="color:#A6ACCD;"> backgroundSize </span><span style="color:#89DDFF;">=</span><span style="color:#A6ACCD;"> </span><span style="color:#82AAFF;">vec2</span><span style="color:#A6ACCD;">(backgroundWidth, backgroundHeight)</span></span>
<span class="line"></span>
<span class="line"><span style="color:#676E95;">-- Called when the UI needs to be generated</span></span>
<span class="line"><span style="color:#89DDFF;">function</span><span style="color:#A6ACCD;"> </span><span style="color:#FFCB6B;">exampleActionElement</span><span style="color:#A6ACCD;">:</span><span style="color:#82AAFF;">initGameElement</span><span style="color:#89DDFF;">(</span><span style="color:#A6ACCD;">gameUI</span><span style="color:#89DDFF;">)</span></span>
<span class="line"><span style="color:#A6ACCD;">    self.</span><span style="color:#FFCB6B;">view</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">=</span><span style="color:#A6ACCD;"> View.</span><span style="color:#82AAFF;">new</span><span style="color:#A6ACCD;">(gameUI.</span><span style="color:#FFCB6B;">view</span><span style="color:#A6ACCD;">)</span></span>
<span class="line"><span style="color:#A6ACCD;">	self.</span><span style="color:#FFCB6B;">view</span><span style="color:#A6ACCD;">.</span><span style="color:#FFCB6B;">size</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">=</span><span style="color:#A6ACCD;"> backgroundSize</span></span>
<span class="line"><span style="color:#A6ACCD;">	self.</span><span style="color:#FFCB6B;">view</span><span style="color:#A6ACCD;">.</span><span style="color:#FFCB6B;">relativePosition</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">=</span><span style="color:#A6ACCD;"> </span><span style="color:#82AAFF;">ViewPosition</span><span style="color:#A6ACCD;">(MJPositionCenter, MJPositionCenter)</span></span>
<span class="line"><span style="color:#89DDFF;">end</span></span>
<span class="line"></span>
<span class="line"><span style="color:#676E95;">-- Called every frame</span></span>
<span class="line"><span style="color:#89DDFF;">function</span><span style="color:#A6ACCD;"> </span><span style="color:#FFCB6B;">exampleActionElement</span><span style="color:#A6ACCD;">:</span><span style="color:#82AAFF;">updateGameElement</span><span style="color:#89DDFF;">(</span><span style="color:#A6ACCD;">gameUI</span><span style="color:#89DDFF;">)</span></span>
<span class="line"><span style="color:#A6ACCD;">	</span></span>
<span class="line"><span style="color:#89DDFF;">end</span></span>
<span class="line"></span>
<span class="line"><span style="color:#89DDFF;">return</span><span style="color:#A6ACCD;"> exampleActionElement</span></span>
<span class="line"></span></code></pre></div>`,18),o=[p];function t(c,r,i,D,A,y){return a(),n("div",null,o)}const m=s(l,[["render",t]]);export{F as __pageData,m as default};
